import "./Option.css";
import React, { useEffect } from "react";
function Option() {
	useEffect(() => {
		OptionDropDown();
	}, []);

	return (
		<div className="OptionParent" id="OptionParent">
			<button className="accordion">Section 1</button>
			<div className="panel">
				<p>
					Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
