import "./Option.css";
import React, { useEffect } from "react";
function Option() {
	useEffect(() => {
		OptionDropDown();
	}, []);

	return (
		<div className="OptionParent" id="OptionParent">
			<div className="OptionTitle">Title</div>
			<div className="OptionContainer">
				<div tabIndex="0" className="Option">
					<div>Name</div>
					<div>Price</div>
				</div>
			</div>
		</div>
	);
}

const OptionDropDown = () => {
	document.getElementById("OptionParent").addEventListener("click", () => {
		document.getElementById("OptionParent").classList.toggle("active");
	});
};

export default Option;
