import "./UpdateItem.css";
import axios, { Axios } from "axios";
import React, { useEffect, useRef, useState } from "react";

function UpdateItem() {
	const [ItemList, SetItemList] = useState([]);
	const GetItemList = () => {
		axios.post("http://localhost:3001/api/GetItems").then((response) => {
			console.log(response.data);
			SetItemList(response.data);
		});
	};

	useEffect(() => {
		GetItemList();
	}, []);

	return (
		<div className="UpdateItemParent">
			<div className="UpdateItemList">
				{ItemList.map((info, index) => {
					return <div key={index}>{info.name}</div>;
				})}
			</div>
			<div className="UpdateItemInfo">ItemInfo</div>
		</div>
	);
}

export default UpdateItem;
