import "./CartPage.css";
import React, { useState } from "react";
import axios from "axios";
function CartPage(props) {
	const [Itemids, SetItemids] = useState([]);

	let ids = localStorage.CartItemIDs;

	let items = Itemids;

	for (let i = 0; i < ids.length; i++) {
		if (ids[i] == "i" && ids[i + 1] == "d") {
			let Item = [];
			for (let j = i + 3; j < i + 12; j++) {
				Item.push(ids[j]);
			}

			items.push(
				Item[0] +
					Item[1] +
					Item[2] +
					Item[3] +
					Item[4] +
					Item[5] +
					Item[6] +
					Item[7] +
					Item[8]
			);
		}
	}

	console.log(Itemids);

	return (
		<div className="CartParent">
			<div className="ItemList">
				<div className="ItemListChild">
					<div className="ItemColumn">Image</div>
					<div className="ItemColumn">Name/desc</div>
					<div className="ItemColumn">Price</div>
				</div>
				{Itemids.map((info, index) => {
					axios
						.post("http://localhost:3001/api/GetItem", { id: info })
						.then((response) => {
							return (
								<div className="ItemListChild" key={index}>
									<div className="ItemColumn">{response}</div>
									<div className="ItemColumn">Name/desc</div>
									<div className="ItemColumn">Price</div>
								</div>
							);
						});
				})}
			</div>
			<div className="OrderInfo">
				<div>Total</div>
				<button className="OrderBtn">Proceed to Checkout</button>
			</div>
		</div>
	);
}

export default CartPage;
