import "./AdminPage.css";
import React, { useState } from "react";
function AdminPage() {
  const [OptionName, SetOptionName] = useState("");
  const [ValueName, SetValueName] = useState("");

  let CurrentOptionValues = [];
  let CurrentOption;
  let optioncount = 0;
  const AddOption = () => {
    var option = document.createElement("button");
    option.className = "AdminOption";
    option.innerHTML = OptionName;
    option.id = optioncount + 1;
    option.addEventListener("click", () => {
      if (window.CurrentOption != undefined) {
        window.CurrentOption.style.backgroundColor = "rgb(200, 200, 200)";
        console.log(window.CurrentOption);
      }

      option.style.backgroundColor = "#FBFBFB";
      window.CurrentOption = option;
      console.log(CurrentOption);
      window.optioncount++;
      console.log(window.optioncount);
    });

    document.getElementById("AdminOptionContainer").appendChild(option);
  };

  return (
    <div className="AdminPage">
      <div className="AdminInParent">
        <label className="AdminLabel">Name: </label>
        <input className="AdminInput"></input>
      </div>
      <div className="AdminInParent">
        <label className="AdminLabel">Description: </label>
        <textarea className="AdminInput" id="description"></textarea>
      </div>
      <div className="AdminInParent">
        <label className="AdminLabel">Price: </label>
        <input type="number" className="AdminInput"></input>
      </div>
      <div className="AdminInParent">
        <div className="AdminOptionContainer" id="AdminOptionContainer"></div>
        <input
          className="AdminInput"
          onChange={(e) => {
            SetOptionName(e.target.value);
          }}
        ></input>
        <button
          onClick={() => {
            AddOption();
          }}
        >
          Add Option
        </button>
        <input
          className="AdminInput"
          onChange={(e) => {
            SetValueName(e.target.value);
          }}
        ></input>
        <button
          onClick={() => {
            CurrentOptionValues.push(ValueName);
            let v = document.createElement("div");
            v.innerHTML = ValueName;
            document.getElementById("CurrentOptionValues").appendChild(v);
          }}
        >
          Add Value to Selected Option
        </button>
        <div id="CurrentOptionValues">
          <h5>CurrentOption Values:</h5>
        </div>
      </div>
    </div>
  );
}

export default AdminPage;
