  const [ItemList, SetItemList] = useState([{ item: "" }]);

  return (
    <div className="Parent">
      <div className="Main" id="Main">
        {ItemList.map(() => {
          <Item></Item>;
        })}
      </div>

      <ItemPage />
    </div>
  );
}

axios.post("http://localhost:3001/api/GetItems").then((response) => {
